-- إضافة حقول الشهادة الثانوية
-- ============================================
-- Step 1: بيانات المتقدم

-- Step 2: معلومات ولي الأمر

-- Step 3: اختيار المواد

-- Step 4: المستندات المطلوبة  

-- التحقق من الإضافة
SELECT 
  st.name_ar,
  (SELECT COUNT(*) FROM service_fields WHERE service_type_id = st.id) as fields_count,
  (SELECT COUNT(*) FROM service_documents WHERE service_type_id = st.id) as documents_count
FROM service_types st
WHERE st.service_id = (SELECT id FROM services WHERE slug = 'education')
ORDER BY st.created_at;
